INSERT INTO `page` (`page_id`, `page_name`, `page_note`, `page_img1`, `label_img1`, `page_img2`, `label_img2`, `page_pdf`, `sub_cat`, `color`) VALUES
(1, 'الولادة والأسرة العريقة', '<p dir="rtl"><span style="color:#B22222"><strong>لا خلاف أنه صلى الله عليه وسلم من سلالة إسماعيل عليه السلام، وقد نشأ من أبوين كريمين يلتقيان في النسب بالجدّ السابع &laquo;كعب&raquo;.</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#B22222"><strong>الاثنين 12 ربيع الأول / 2 نيسان (أبريل) 570م</strong></span><br />\r\n&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#006400"><strong>أبوه</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#006400">عبد الله بن عبد المطلب بن هاشم بن عبد مناف بن قصي بن كلاب بن مرة بن كعب.<br />\r\nوقد توفي أبوه عبد الله في يثرب (المدينة المنورة) وذلك أثناء عودة عبد الله من تجارة له إلى الشام &nbsp;ودفن فيها (بينما محمد صلى الله عليه وسلم في بطن أمه) ، فولد محمد صلى الله عليه وسلم يتيماً. (والدراسات العلمية اليوم تثبت أن أيتام الأب فرصتهم للقيادة أعلى من المتوسط العام في البشر، لأنهم يتحملون المسؤولية مبكراً وفي نفس الوقت لم يفقدوا حنان الأم).</span><br />\r\n&nbsp;</p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#006400"><strong>أمه</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#006400">آمنة بنت وهب بن عبد مناف بن زهرة بن كلاب بن مرة بن كعب.<br />\r\nوقد ماتت وعمره صلى الله عليه وسلم ست سنوات ،ودفنت ما بين مكة والمدينة حيث كانت عائدة من المدينة بعد زيارتها لأقاربها بني النجار. (والدراسات القيادية كذلك تثبت أن الشخصية القيادية تتشكل في السنوات الأولى من حياة الإنسان وخاصة بين سن الثانية والسادسة إذا كانت الناحية العاطفية مستقرة).</span></p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#006400">عدنان &laquo;الجد العشرون&raquo;586 ق.م<br />\r\nهو محمد بن عبد الله بن عبد المطلب بن هاشم بن عبد مناف بن قصي بن كلاب بن مرة بن كعب بن لؤي بن غالب بن فهر (وفهر هو قريش) بن مالك بن النضر بن كنانة بن خزيمة بن مدركة بن إلياس بن مضر بن نزار بن معد بن عدنان<br />\r\nوالنسب إلى عدنان محفوظ ومشهور، والمعرفة به دقيقة، أما النسب بعد عدنان فليس ثابتاً فقد ورد عن ابن عباس وابن مسعود رضي الله عنهما في الأثر: &laquo;كذب النسابون ما بعد عدنان&raquo;.</span><br />\r\n&nbsp;</p>\r\n', '1_1.jpg', NULL, '1_2.jpg', NULL, NULL, 1, NULL),
(2, 'الولادة والأسرة العريقة', '<p dir="rtl"><span style="color:#4B0082"><strong>الجد الرابع قصي 440م</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">استطاعت قبيلة خزاعة السيطرة على مكة (كما ذكرنا في الباب الأول) وانتزعت قيادة العرب من قريش الذين ظلوا في مكة يتطلعون إلى عودة مكة وسيادة العرب إليهم ولكنهم آنذاك خاضعين لخزاعة، ولكن ذلك تغير على يد قصي بن كلاب أعظم زعماء قريش في الجاهلية.<br />\r\nوتبدأ القصة عندما عاد قصي بن كلاب إلى مكة فترة شبابه (بعد أن عاش طفولته خارجها مع أمه التي تزوجت رجلاً من قضاعة) وامتاز قصي بخلقه وعقله ومروءته، فأعجب به سيد مكة الخُزاعي، وزوّجه ابنته، وأوصى إليه بولاية البيت، ولما مات نازع قصي خزاعة على الملك وكادت تحدث الحرب بينهما، إلى أن اتفق الطرفان على تحكيم أحد حكماء العرب، وبيّن قصي أنه أحق بالملك لأن قبيلته هي التي أسست مكة، فحكم له الحكيم بذلك، وانتقلت بذلك &nbsp;السيادة إلى قريش، وصار قصي سيد العرب فأكرم خزاعة وتصالحا.</span><br />\r\n&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082"><strong>دار الندوة في مكة </strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">جمع قصي بطون قريش، وبنى داراً واسعة سماها دار الندوة للتباحث والمشورة في أحوال قبيلة قريش، ووزع أدوار الرياسة على زعمائها وعلى فروع قريش، وهي أمور تختص بالكعبة وشرف خدمتها (الحجابة، وإطعام وسقاية الحجاج)، وأمور تتعلق بالإدارة والحروب والعلاقات مع القبائل والدول (القيادة السياسية، السِّفارة الدبلوماسية، واللواء بقيادة الحروب) وبهذا شارك الجميع وتوحدت قريش على ذلك.</span></p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082"><strong>هاشم جده الثاني</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">تولى السقاية وإطعام الحجاج حين اقتسمت قريش المناصب والأدوار، وكان موسراً ذا شرفٍ كبير وهو أول من أطعم الثريد بمكة، وكان اسمه عمرو ثم سمي هاشماً لهشمه الخبر (أي تكسيره وتوزيعه على الحجاج)، كما أنه أول من سن رحلة الشتاء والصيف لقريش وفيه يقول الشاعر:</span><br />\r\n<span style="color:#B22222"><strong>عمرو الذي هشم الثريد لـقومه &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; قـوم بـمـكـة مـسنتين عـجــاف<br />\r\nسـنـت إلـيه الرحـلتان كـــــلاهمـا &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; سـفر الـشتـاء ورحلة الأصياف</strong></span><br />\r\n<span style="color:#4B0082">وبعد وفاة هاشم تولى السقاية والرفادة بعده ابنه المطلب وكان شريفا مطاعا، وكانت قريش تسميه الفياض لكرمه وفيض سخائه.<br />\r\nوكان هاشم قبل وفاته تزوج بامرأة من مدينة يثرب (حين مر بها)، فولدت له عبدالمطلب وهي عند أهلها هناك. ومات هاشم بغزة في فلسطين أثناء سفره للتجارة.</span><br />\r\n&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082"><strong>الجدّ الأول عبد المطلب 497م&nbsp; 530م</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">ولد في يثرب سنة 497 م وكان اسمه شيبة، فلما كبر ذهب إليه أخوه المطلب وجاء به إلى مكة فلما رآه الناس مع المطلب ظنوه عبدًا للمطلب فقالوا: عبد المطلب، فصار اسما له.<br />\r\nأصبح عبد المطلب بعد وفاة أخيه المطلب زعيم قريش، وكان عليه السقاية للحجاج، وكانت مهمةً شاقة لقلة المياه (حيث كانت بئر زمزم قد غطيت من أيام نزاع قريش مع خزاعة)، فاهتدى يوماً إلى بئر زمزم (وربما رأى مكانها من كلام أُلهمه في رؤيا في المنام) وزاد ذلك في شرفه بين العرب.</span><br />\r\n&nbsp;</p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n', '2_1.jpg', '', '', '', NULL, 1, ''),
(3, 'بعض نسب قريش', NULL, '3_1.png', '', '', '', NULL, 1, ''),
(4, 'قبيلته: (قريش)', '<p dir="rtl"><span style="color:#4B0082">قريش‭ ‬هو‭ ‬اسم‭ ‬آخر‭ ‬لأحد‭ ‬أجداد‭ ‬النبي‭ ‬صلى‭ ‬الله‭ ‬عليه‭ ‬وسلم‭ ‬وهو‭ (‬فهر‭) ‬فسميت‭ ‬القبيلة‭ ‬نسبة‭ ‬إليه‭.‬</span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">أبرهة‭ ‬الاشرم‭ ‬يقود‭ ‬جيشا‭ ‬الفيلة‭ ‬لهدم‭ ‬الكعبة‭.‬</span></p>\r\n', '4_1.jpg', '', '', '', NULL, 1, ''),
(5, 'حادثة الفيل', '<p dir="rtl"><span style="color:#4B0082">أراد‭ ‬أبرهة‭ ‬الحبشي‭ ( ‬الحاكم‭ ‬العام‭ ‬لليمن‭ ‬نيابة‭ ‬عن‭ ‬ملك‭ ‬الحبشة‭ ‬النجاشي‭ ) ‬أن‭ ‬يصرف‭ ‬العرب‭ ‬عن‭ ‬الحج‭ ‬إلى‭ ‬الكعبة‭ ‬ليحجوا‭ ‬إلى‭ ‬كنيسة‭ ‬بناها‭ ‬هو‭ ‬في‭ ‬اليمن‭ (‬التي‭ ‬احتلها‭ ‬الأحباش‭ ‬من‭ ‬إفريقيا‭) ‬فدخل‭ ‬الكنيسة‭ ‬رجل‭ ‬عربي‭ ‬من‭ ‬بني‭ ‬كنانة‭ ‬ليلا‭ ‬فلطخها‭ ‬بالعذرة‭ ( ‬القذر‭ ‬الذي‭ ‬يخرج‭ ‬من‭ ‬الإنسان‭)‬،‭ ‬فلما‭ ‬علم‭ ‬أبرهة‭ ‬بذلك‭ ‬غضب‭ ‬غضبا‭ ‬شديدا‭ ‬وقاد‭ ‬جيشاً‭ ‬عظيماً‭ ‬في‭ ‬مقدمته‭ ‬فيل‭ ‬عظيم‭ ‬ليهدم‭ ‬الكعبة،‭ ‬ولما‭ ‬اقترب‭ ‬من‭ ‬مكة‭ ‬خرجت‭ ‬قريش‭ ‬فتفرقوا‭ ‬في‭ ‬رؤوس‭ ‬الجبال،‭ ‬وأخذ‭ ‬جيشه‭ ‬مئتي‭ ‬بعير‭ ‬لعبد‭ ‬المطلب‭ (‬جد‭ ‬النبي‭ ‬صلى‭ ‬الله‭ ‬عليه‭ ‬وسلم‭)‬،‭ ‬فجاء‭ ‬عبد‭ ‬المطلب‭ ‬إلى‭ ‬أبرهة‭ ‬يطالب‭ ‬بإبله،‭ ‬وكان‭ ‬عبدالمطلب‭ ‬رجلاً‭ ‬وسيماً‭ ‬جميلاً‭ ‬مهيباً،‭ ‬فنزل‭ ‬أبرهة‭ ‬عن‭ ‬سريره‭ ‬وجلس‭ ‬معه‭ ‬على‭ ‬بساطه‭ (‬إكراماً‭ ‬له‭ ‬من‭ ‬جهة‭ ‬وحتى‭ ‬لا‭ ‬يجلسه‭ ‬معه‭ ‬على‭ ‬عرشه‭ ‬من‭ ‬جهة‭ ‬أخرى‭)‬،‭ ‬وكان‭ ‬أبرهة‭ ‬يظنه‭ ‬قادماً‭ ‬ليكلمه‭ ‬في‭ ‬أمر‭ ‬الكعبة،‭ ‬فكلمه‭ ‬عبدالمطلب‭ ‬بشأن‭ ‬الإبل‭ ‬مطالباً‭ ‬بإرجاعها‭ ‬إليه،‭ ‬ولم‭ ‬يحاول‭ ‬إقناعه‭ ‬بعدم‭ ‬هدم‭ ‬الكعبة،‭ ‬وعندها‭ ‬نزلت‭ ‬مكانة‭ ‬عبدالمطلب‭ ‬في‭ ‬عين‭ ‬أبرهة‭ ‬وعجب‭ ‬من‭ ‬أمره‭ ‬كيف‭ ‬لايحاول‭ ‬ثنيه‭ ‬عن‭ ‬هدم‭ ‬البيت‭ ‬المقدس‭ ‬عنده‭ ‬وعند‭ ‬العرب‭.‬</span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">فقال‭ ‬له‭ ‬عبد‭ ‬المطلب‭: ‬أنا‭ ‬رب‭ ‬الإبل‭ (‬أي‭ ‬صاحبها‭)‬،‭ ‬وإن‭ ‬للبيت‭ ‬رباً‭ ‬سيمنعه‭ (‬سيحميه‭).‬</span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">فقال‭ ‬له‭ ‬أبرهة‭: ‬ما‭ ‬كان‭ ‬ليمتنع‭ ‬البيت‭ ‬مني‭ (‬أي‭ ‬لن‭ ‬يحمي‭ ‬مني‭ ‬الكعبة‭ ‬أحد‭).‬</span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">قال‭ ‬عبدالمطلب‭ ‬له‭: ‬أنت‭ ‬وذاك‭.‬</span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">فحمى‭ ‬الله‭ ‬بيته‭ ‬من‭ ‬أبرهة،‭ ‬وكانت‭ ‬نهايته‭ ‬كما‭ ‬ذكر‭ ‬القرآن‭ ‬الكريم‭ ‬بأن‭ ‬دمره‭ ‬وجيشه‭ ‬بإلقاء‭ ‬حجارة‭ ‬صغيرة‭ ‬تحملها‭ ‬طيور‭ ‬صغيرة‭ ‬وكثيرة،‭ ‬واشتهرت‭ ‬القصة‭ ‬عند‭ ‬العرب‭ ‬مما‭ ‬زاد‭ ‬من‭ ‬تعظيم‭ ‬الكعبة‭ ‬في‭ ‬قلوبهم‭. ‬ووصف‭ ‬القرآن‭ ‬الكريم‭ ‬هذه‭ ‬القصة‭ ‬في‭ ‬سورة‭ ‬الفيل‭: {‬أَلَمْ‭ ‬تَرَ‭ ‬كَيْفَ‭ ‬فَعَلَ‭ ‬رَبُّكَ‭ ‬بِأَصْحَابِ‭ ‬الْفِيلِ‭ (‬1‭) ‬أَلَمْ‭ ‬يَجْعَلْ‭ ‬كَيْدَهُمْ‭ ‬فِي‭ ‬تَضْلِيلٍ‭ (‬2‭) ‬وَأَرْسَلَ‭ ‬عَلَيْهِمْ‭ ‬طَيْراً‭ ‬أَبَابِيلَ‭ (‬3‭) ‬تَرْمِيهِمْ‭ ‬بِحِجَارَةٍ‭ ‬مِنْ‭ ‬سِجِّيلٍ‭ (‬4‭) ‬فَجَعَلَهُمْ‭ ‬كَعَصْفٍ‭ ‬مَأْكُولٍ‭(‬5‭)} (‬سورة‭ ‬الفيل‭) ‬</span></p>\r\n\r\n<p dir="rtl"><span style="color:#4B0082">وكانت‭ ‬هذه‭ ‬الحادثة‭ ‬قبل‭ ‬ولادة‭ ‬النبي‭ ‬صلى‭ ‬الله‭ ‬عليه‭ ‬وسلم‭ ‬بخمسين‭ ‬يوما‭.‬</span></p>\r\n', '', '', '', '', NULL, 1, ''),
