(33, 'أحداث عالمية', '<p dir="rtl"><span style="color:#B22222"><strong>600م</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#808080">ظهور‭ ‬التصوير‭ ‬الكلاسيكي‭ ‬لبوذا‭&nbsp; (‬في‭ ‬هيئة‭ ‬ممارسة‭ ‬اليوغا‭) ‬في‭ ‬بيهار،‭ ‬شمال‭ ‬الهند</span></p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#B22222"><strong>600م</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#808080">ظهور‭ ‬مجموعة‭ ‬الترانيم‭ ‬الكنسية‭ ‬‮&laquo;‬<strong>Antiphonar&rdquo;</strong>‭ ‬التي‭ ‬أعدّها‭ ‬البابا‭ ‬غريغوري‭ ‬الأوّل‭ ‬<strong>Gregory</strong>‭ ‬<strong>I</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir="rtl"><strong><span style="color:#B22222">604م</span></strong></p>\r\n\r\n<p dir="rtl"><span style="color:#808080">أوّل‭ ‬جرس‭ ‬كنيسة‭ ‬في‭ ‬روما</span></p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n\r\n<p dir="rtl"><strong><span style="color:#B22222">606م</span></strong></p>\r\n\r\n<p dir="rtl"><span style="color:#808080">كان‭ ‬اجتياز‭ ‬امتحاناتٍ‭ ‬معينة‭ ‬مفروضاً‭ ‬على‭ ‬المتقدّمين‭ ‬للوظائف‭ ‬العامة‭ ‬في‭ ‬الصين</span></p>\r\n\r\n<p dir="rtl">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir="rtl"><span style="color:#B22222"><strong>602م</strong></span></p>\r\n\r\n<p dir="rtl"><span style="color:#808080">سلسلة‭ ‬من‭ ‬الحروب‭ ‬تنشب‭ ‬بين‭ ‬إمبراطورية‭ ‬بيزنطة‭ ‬النصرانية‭ ‬والإمبراطورية‭ ‬الساسانية‭ ‬الفارسية‭ ‬المجوسية</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '33_1.jpg', '', '', '', NULL, 2, ''),
